        console.log(ref.current.value)
    }
    return (
        
        <div className="row m-2 justify-content-center">
           <div className="col-md-6"><input type="text"  ref={ref}
        
        onChange={handleChange} className="w-100"/></div>
        </div>
            
              );
}

export default SearchBar;
