      const handleOnClick = (e) => {
        e.preventDefault();

        setTextBox(!textBox)
      }

      const handleChange = () => {
       // e.preventDefault();

        //setTextBox(!textBox)
      }

      console.log("textBox",textBox)
     return (
        
        <div  className=" col-md-3 col-lg-4 mb-4">
          <div className="card  h-100">
            <div className="card-header">
              {data.title} <button onClick={handleOnClick}>Edit</button>
              
            </div>
            <div className="card-body">
            { textBox && 
                    <form>
                      <input type="text" name="title" onChange={handleChange} value={ data.title } />

                    </form>
              }
              <h5 className="card-title">Special title treatment</h5>
              <p className="card-text" style={{ display : textBox ? 'block' : 'none' }}>{data.body}</p>
              <a href="#" className="btn btn-primary">Go somewhere</a>
            </div>
          </div>
         
          
        </div>
    );
}

export default CardDetails;
