import React, { useState } from 'react';

import { useDispatch } from 'react-redux';
import { updatePost } from '../store/post'
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const CardDetails = ({data}) => {
  
      const [textBox,setTextBox] = useState(false);


      const [inputData,setinputData] = useState(data.title);
      const [textAreaData,settextAreaData] = useState(data.body);

      const dispatch = useDispatch();

      const handleOnClick = (e) => {
        e.preventDefault();

        setTextBox(!textBox)
      }

      

      const divStyle = {
        display : textBox ? 'none' : 'block'
        
      };
      
      const handleSubmit = (e) => {
          e.preventDefault();
          console.log("Submitted");

          let formSubmit = true;

          if(!inputData) {
            formSubmit = false;
          }



          let formData = {
            id: data.id,
            title: inputData,
            body : textAreaData
          }
          dispatch(updatePost(formData));

      }

      const handleChange = (e) => {
          
          setinputData(e.target.value || '' );
      }

      //console.log("textBox",textBox)
     return (
        
        <div  className=" col-md-3 col-lg-4 mb-4">
          <div className="card  h-100">
            <div className="card-header">
              {data.title} <button onClick={handleOnClick}>Edit</button>
              
            </div>
            <div className="card-body">

            { textBox && 
                    <form  onSubmit={handleSubmit}>
                      <div className="mb-3">
                          <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
                          <input  type="text" 
                                  className="form-control" 
                                  id="exampleFormControlInput1" 
                                  onChange = { (e) => setinputData(e.target.value) }
                                  value={ inputData }
                                  placeholder="name@example.com"/>
                      </div>
                      <div className="mb-3">
                        <label htmlFor="exampleFormControlTextarea1" className="form-label">Example textarea</label>
                        <textarea className="form-control" id="exampleFormControlTextarea1" 
                                  onChange={(e)=> settextAreaData(e.target.value)} 
                                  rows="3" value={ textAreaData } />
                      </div>
                      <button type="submit" className="btn btn-primary">Primary</button>
                    </form> || <><h5 className="card-title">Special title treatment</h5><p className="card-text" style={ divStyle }>{data.body}</p></>
              }
              
              
              
            </div>
          </div>
         
          
        </div>
    );
}

export default CardDetails;
