import { createReducer } from "@reduxjs/toolkit";

const userSlice = createReducer({
    name : "user",
    initialState : { value : { name : 'Hi'} },
    reducer : {

        details : (state,action) => {
            state.value = action.payload
        }

    }
})

export default userSlice.reducer;
