import { createSlice } from '@reduxjs/toolkit';
import { getDataFromLocalStorage,saveToLocalStorage,checkLocalStorageData } from '../util';

export const postSlice = createSlice({
    name: 'post',
    initialState: {
      postList : [],
      value: 0,
    },
    reducers: {
      getAllPost:(name) => {
        return name
      },
      setAllPost:(state,action) => {
        //console.log("action",action.payload);
        console.log(getDataFromLocalStorage());
        if(!getDataFromLocalStorage()) {

          saveToLocalStorage(action.payload);
          state.postList = getDataFromLocalStorage();

        } else {
          state.postList = [];
        }
        //state.postList = !getDataFromLocalStorage() ? saveToLocalStorage(action.payload) : [];
        state.postList = action.payload;
      }
    },
  })
  
  
  export const { getAllPost,setAllPost } = postSlice.actions
  
  export default postSlice.reducer
