import React,{ useState,useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';

const SearchBar = ({ onQuery }) => {

    let postList = useSelector((state) => state.post.postList)
    
    const ref = useRef();
    console.log(ref,'www');
    const handleChange = () => {
        onQuery(ref.current.value);
        postList = ref && ref.current && ref.current.value && postList.filter((item) => {
            return item.title.toLowerCase().includes(ref.current.value.toLowerCase());
        });
    }

    
    console.log(postList,'ddd');

    return (
        
        <div className="row m-2 justify-content-center">
           <div className="col-md-6 autocomplete">
                <input  type="text"  
                        ref={ref}
                        onChange={handleChange} className="w-100"/>
                        <ul id="myUL">
                            {
                                 ref.current.value && postList && postList.map(item=><li><a href="#">Adele</a></li>)
                            }
                            
                        </ul>
            </div>
            
        </div>
            
    );
}

export default React.memo(SearchBar);
